    this.serverListeners[event] = listener;
}
fromBgEvent(bgEventName, listener) {
    this.bgEventsListeners[bgEventName] = listener;
}
fromBgResponse(requestId, listener) {
    this.bgRequestsListeners[requestId] = listener;
}
